te1: AD+ANoD=AA;
te2: BD+BNoD=BB;
te3: CD+CNoD=CC;
te4: DD+noD=100;
te4: AD+BD+CD=DD;
te5: ANoD+BNoD+CNoD=noD;
te6: AA+BB+CC=100;
te7:DCondA+noDCondA=1;
te8:DCondB+noDCondB=1;
te9:DCondC+noDCondC=1;

tec1: ACondD=AD/DD;
tec2: ACondNoD=ANoD/noD;
