tec6: CCondNoD=CNoD/noD;

tec1_1: DCondA=AD/AA;
tec2_1: noDCondA=ANoD/AA;
tec3_1: DCondB=BD/BB;
tec4_1: noDCondB=BNoD/BB;
tec5_1: DCondC=CD/CC;
tec6_1: noDCondC=CNoD/CC;

