    tec1,tec2,tec3,tec4,tec5,tec6,
    tec1_1,tec2_1,tec3_1,tec4_1,tec5_1,tec6_1,
    r1,r2,r3,r4,r5],
    [AA,AD,ANoD,BB,BD,BNoD,CC,CD,CNoD,DD,noD,ACondD,ACondNoD,
    BCondD,BCondNoD,CCondD,CCondNoD,
    DCondA,DCondB,DCondC,
    noDCondA,noDCondB,noDCondC]);

